import React from 'react'
import { NavLink, Link } from 'react-router-dom'
import './style.css'

const Post = (props) => {
   return (
      <div className='post' >
         <div className='inner_post'>
            <h3>Post {props.id}</h3>
            <p className='post_text'>{props.text}</p>
         </div>
         <Link to={`/userData/${props.user}`}>Profile</Link>
         <NavLink to={`post/${props.id}`}>Check Post</NavLink>
      </div>
   )
}

export default Post