import { START_FETCH_DATA_USER, SUCCESS_FETCH_DATA_USER, ERROR_FETCH_DATA_USER, LOGOUT } from 'redux/actions/constants'

export function startFetchUserData() {
   return {
      type: START_FETCH_DATA_USER
   }
}

export function successFetchUserData(token) {
   return {
      type: SUCCESS_FETCH_DATA_USER,
      payload: {
         token
      }
   }
}

export function errorFetchUserData(error) {
   return {
      type: ERROR_FETCH_DATA_USER,
      payload: {
         error
      }
   }
}

export function logout() {
   localStorage.removeItem('token')
   return {
      type: LOGOUT,
      
   }
}