import { GET_COMMENTS, CLEAR_COMMENTS, ADD_COMMENT } from 'redux/actions/constants'

export function getComments(data) {
   return {
      type: GET_COMMENTS,
      payload: {
         data
      }
   }
}

export function addComment(data) {
   return {
      type: ADD_COMMENT,
      payload: {
         data
      }
   }
}

export function clearComments() {
   return {
      type: CLEAR_COMMENTS,
   }
}