import { GET_USER, GET_AUTH_USER, CLEAR_USER, CLEAR_AUTH_USER } from 'redux/actions/constants'

export function getUser(data) {
   return {
      type: GET_USER,
      payload: {
         data
      }
   }
}

export function clearUser(data) {
   return {
      type: CLEAR_USER,
      payload: {
         data
      }
   }
}

export function getAuthUser(data) {
   return {
      type: GET_AUTH_USER,
      payload: {
         data
      }
   }
}



export function clearAuthUser() {
   return {
      type: CLEAR_AUTH_USER,
   }
}