import { GET_COMMENTS, CLEAR_COMMENTS } from 'redux/actions/constants'

const initialState = {
   commentsSelectedPost: null,
}

export default function commentsReducer(state = initialState, action) {
   switch (action.type) {
      case GET_COMMENTS: {
         const { data } = action.payload
         return {
            ...state, 
            commentsSelectedPost: data
         }
      }
      case CLEAR_COMMENTS: {
         return initialState
      }
   
      default:
         return state;
   }
}