import { GET_USER, GET_AUTH_USER } from 'redux/actions/constants'

const authUserId = JSON.parse(localStorage.getItem('userId'))
const initialState = authUserId
   ? {
      currentUserData: authUserId,
      findUser: null
   }
   : {
      currentUserData: null,
      findUser: null
   }

// const initialState = {
//    currentUserData: null,
//    findUser: null
// }

export default function userReducer(state = initialState, action) {
   switch (action.type) {
      case GET_USER: {
         const { id, name } = action.payload
         return {
            ...state,
            findUser: {
               id, name
            }
         }
      }
      case GET_AUTH_USER: {
         const { id, name, email, created_at } = action.payload
         return {
            ...state,
            currentUserData: {
               id, name, email, created_at
            }
         }
      }
      default:
         return state;
   }
}