import React from 'react'
import { Switch, Route } from 'react-router-dom'

import Auth from 'containers/Auth'
import HomePage from 'containers/HomePage'
import Form from 'containers/Form'
import PostsControl from 'containers/PostControl'
import ProfileUser from 'containers/ProfileUser'

export default function () {
   return (
      <Switch>
         <Route path='/' component={HomePage} exact />
         <Route path='/myposts' component={HomePage} />
         <Route path='/register' component={Auth} />
         <Route path='/login' component={Auth} />
         <Route path='/post/:slug' component={PostsControl} exact />
         <Route path='/createNewPost' component={Form} />
         <Route path='/updatePost/:slug' component={Form} exact />
         <Route path='/userData/my' component={ProfileUser} />
         <Route path='/userData/:slug' component={ProfileUser} exact />
      </Switch>
   )
}

